import React from 'react';
import { useNavigate } from 'react-router-dom';

import backgroundimage from '../assets/landingbg.jpeg'
const styles = {
    container: {
        position: 'relative',
        width: '100%',
        height: '100vh',
    },
    background: {
        position: 'absolute',
        width: '100%',
        height: '100%',
        backgroundImage: `url(${backgroundimage})`,
        backgroundSize: 'cover',
    },
    content: {
        position: 'absolute',
        top: '50%',
        left: '50%',
        transform: 'translate(-50%, -50%)',
        textAlign: 'center',
        column: '2'
    },
};

const LandingBG = () => {
    const history = useNavigate();

    const handleShopClick = () => {
        history.push('/shop');
    };

    const handleAboutClick = () => {
        history.push('/about');
    };

    return(
        <div style={styles.container}>
            <div style={styles.left} onClick={handleShopClick} />
            <div style={styles.right} onClick={handleAboutClick} />
        </div>
    );
}


export default LandingBG;
