import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

import shopside from '../assets/shopside.jpg';
import aboutside from '../assets/aboutside.jpg';


const Background = () => {
  const [leftHover, setLeftHover] = useState(false);
  const [rightHover, setRightHover] = useState(false);
  const navigate = useNavigate();

  return (
    <div style={backgroundContainer}>
      <div
        style={leftContainer}
        onMouseEnter={() => setLeftHover(true)}
        onMouseLeave={() => setLeftHover(false)}
        onClick={() => navigate('/shop')}
      >
        <img
          src={shopside}
          alt="left background"
          style={leftImage}
        />
        {leftHover && (
          <div style={leftText}>
            <p style={text}>Go to Shop</p>
          </div>
        )}
      </div>
      <div
        style={rightContainer}
        onMouseEnter={() => setRightHover(true)}
        onMouseLeave={() => setRightHover(false)}
        onClick={() => navigate('/about')}
      >
        <img
          src={aboutside}
          alt="right background"
          style={rightImage}
        />
        {rightHover && (
          <div style={rightText}>
            <p style={text}>Go to About</p>
          </div>
        )}
      </div>
    </div>
  );
};

const backgroundContainer = {
  display: 'flex',
  height: '100vh',
  width: '100vw'
};

const leftContainer = {
  width: '50%',
  position: 'relative',
  backgroundColor: leftHover ? 'rgba(0, 0, 0, 0.5)' : 'transparent'
};

const leftImage = {
  width: '100%',
  height: '100%',
  objectFit: 'cover'
};

const leftText = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)'
};

const rightContainer = {
  width: '50%',
  position: 'relative',
  backgroundColor: rightHover ? 'rgba(0, 0, 0, 0.5)' : 'transparent'
};

const rightImage = {
  width: '100%',
  height: '100%',
  objectFit: 'cover'
};

const rightText = {
  position: 'absolute',
  top: '50%',
  right: '50%',
  transform: 'translate(50%, -50%)'
};

const text = {
  color: 'white',
  fontSize: '24px'
};

export default Background;
